import React, { useState, useEffect } from 'react';
import './Bingo.css';

const Bingo = () => {
  const [sortedNumbers, setSortedNumbers] = useState(new Set());
  const [lastFiveNumbers, setLastFiveNumbers] = useState([]);
  const [displayNumber, setDisplayNumber] = useState(null);
  const [gameOver, setGameOver] = useState(false);
  const [showVideo, setShowVideo] = useState(false);

  // Números que desencadenan la visualización del video
  const specialNumbers = [10, 1, 23, 45, 34, 50, 22, 43, 11];

  // Función para generar un número aleatorio
  const generateRandomNumber = () => {
    if (gameOver) return;

    const availableNumbers = [...Array(50).keys()].map(i => i + 1).filter(num => !sortedNumbers.has(num));

    if (availableNumbers.length === 0) {
      setGameOver(true);
      setDisplayNumber(null);
      return;
    }

    const randomIndex = Math.floor(Math.random() * availableNumbers.length);
    const randomNumber = availableNumbers[randomIndex];

    setSortedNumbers(prev => new Set(prev).add(randomNumber));
    setDisplayNumber(randomNumber);

    // Mostrar la modal de video solo si el número es especial
    if (specialNumbers.includes(randomNumber)) {
      setShowVideo(true);
      // Cierra el video automáticamente después de 40 segundos
      setTimeout(() => setShowVideo(false), 40000);
    }

    // Actualizar la lista de los últimos cinco números
    setLastFiveNumbers(prev => {
      const updatedList = [randomNumber, ...prev];
      return updatedList.slice(0, 5);
    });
  };

  // Detectar la tecla "Enter" para generar un número aleatorio
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'Enter') {
        generateRandomNumber();
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [sortedNumbers, gameOver]);

  // Función para cerrar la modal manualmente
  const handleCloseVideo = () => {
    setShowVideo(false);
  };

  // Reiniciar el juego al hacer clic en "Gracias por participar"
  const resetGame = () => {
    setSortedNumbers(new Set());
    setLastFiveNumbers([]);
    setDisplayNumber(null);
    setGameOver(false);
    setShowVideo(false);
  };

  return (
    <div className="bingo-container">
      {/* Modal de video a pantalla completa */}
      {showVideo && (
        <div className="video-modal" onClick={handleCloseVideo}>
          <div className="video-modal-content">
            <div className="modal-number">{displayNumber}</div>
            <iframe
              className="video-fullscreen"
              src="https://www.youtube.com/embed/uBFdSyDkPOU?autoplay=1"
              title="YouTube video player"
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            ></iframe>
          </div>
        </div>
      )}

      {/* Mensaje de agradecimiento al finalizar */}
      {gameOver ? (
        <div className="game-over-message" onClick={resetGame}>
          Gracias por participar
        </div>
      ) : (
        <>
          <div className="left-section">
            <div className={`display-number ${displayNumber !== null ? 'animate' : ''}`} key={displayNumber}>
              {displayNumber !== null ? displayNumber : 'Presiona Enter'}
            </div>
          </div>

          <div className="bingo-card">
            {[...Array(50).keys()].map(i => {
              const number = i + 1;
              const isSorted = sortedNumbers.has(number);

              return (
                <div 
                  key={number} 
                  className={`bingo-cell ${isSorted ? 'sorted' : ''}`}
                >
                  {number}
                </div>
              );
            })}
          </div>
        </>
      )}
    </div>
  );
};

export default Bingo;
