import React, { useState, useEffect, useCallback } from 'react';
import './App.css';

const BingoBoard = ({ markedNumbers }) => {
  return (
    <div className="bingo-board">
      {[...Array(90).keys()].map(i => {
        const number = i + 1;
        const isMarked = markedNumbers.includes(number);
        return (
          <div
            key={number}
            className={`bingo-number ${isMarked ? 'marked' : ''}`}
          >
            {number.toString().padStart(2, '0')}
          </div>
        );
      })}
    </div>
  );
};

const videoUrls = {
  1: 'https://www.youtube.com/embed/video1',
  2: 'https://www.youtube.com/embed/video2',
  3: 'https://www.youtube.com/embed/video3',
  // Añadir URLs para cada número hasta el 90
  90: 'https://www.youtube.com/embed/video90',
};

const App = () => {
  const [currentNumber, setCurrentNumber] = useState(null);
  const [previousNumbers, setPreviousNumbers] = useState([]);
  const [markedNumbers, setMarkedNumbers] = useState([]);
  const [showLiniaCantada, setShowLiniaCantada] = useState(false);
  const [animate, setAnimate] = useState(false);
  const [emojis, setEmojis] = useState([]); // Estado para los emojis de lluvia
  const [currentVideoUrl, setCurrentVideoUrl] = useState(null);

  const generateRandomNumber = useCallback(() => {
    const availableNumbers = [...Array(90).keys()].map(i => i + 1).filter(num => !previousNumbers.includes(num));
    if (availableNumbers.length === 0) return;

    const randomIndex = Math.floor(Math.random() * availableNumbers.length);
    const randomNumber = availableNumbers[randomIndex];

    setCurrentNumber(randomNumber);
    setPreviousNumbers(prev => [randomNumber, ...prev].slice(0, 5));
    setMarkedNumbers(prev => [...prev, randomNumber]);

    setAnimate(true);
    setTimeout(() => setAnimate(false), 500);

    // Asignar URL de video para el número actual
    setCurrentVideoUrl(videoUrls[randomNumber] || null);
  }, [previousNumbers]);

  useEffect(() => {
    const handleKeyPress = (event) => {
      if (event.key === 'Enter') {
        generateRandomNumber();
      }
      if (event.key === 'l' || event.key === 'L') {
        setShowLiniaCantada(prev => !prev);
        if (!showLiniaCantada) triggerEmojiRain(); // Iniciar la lluvia de emojis
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [generateRandomNumber, showLiniaCantada]);

  const triggerEmojiRain = () => {
    const emojiSet = ['🎉', '🥳', '🎈'];
    const newEmojis = Array.from({ length: 30 }, () => ({
      id: Math.random(),
      emoji: emojiSet[Math.floor(Math.random() * emojiSet.length)],
      left: Math.random() * 100, // Posición horizontal aleatoria
      delay: Math.random() * 2, // Retraso aleatorio para efecto en cascada
    }));
    setEmojis(newEmojis);

    // Limpiar los emojis después de 3 segundos
    setTimeout(() => setEmojis([]), 3000);
  };

  return (
    <div className="app-container">
      <div className="current-number-box">
        <div className={`current-number ${animate ? 'animate-flash' : ''}`}>
          {currentNumber !== null ? currentNumber.toString().padStart(2, '0') : "00"}
        </div>
      </div>

      <div className="side-box">
        <div className="side-content">
          {previousNumbers.slice(1, 6).map((num, index) => (
            <span key={index} className={`previous-number opacity-${index}`}>
              {num.toString().padStart(2, '0')}
            </span>
          ))}
        </div>
      </div>

      <div className="large-box">
        <BingoBoard markedNumbers={markedNumbers} />
      </div>

      <div className="small-box">
        <span>01/06 - QUINA FAMA</span>
        <span className={`linia-cantada ${showLiniaCantada ? 'show' : ''}`}>LÍNIA CANTADA!! 🎉🎉</span>
      </div>

      <div className="additional-box">
        <div className="additional-content">Additional Content Here</div>
      </div>

      {/* Contenedor de emojis de lluvia */}
      <div className="emoji-rain-container">
        {emojis.map((emoji) => (
          <div
            key={emoji.id}
            className="falling-emoji"
            style={{
              left: `${emoji.left}%`,
              animationDelay: `${emoji.delay}s`,
            }}
          >
            {emoji.emoji}
          </div>
        ))}
      </div>

      {/* Video si existe para el número actual */}
      {currentVideoUrl && (
        <div className="video-box">
          <iframe
            src={`${currentVideoUrl}?autoplay=1`}
            title="Bingo Video"
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          ></iframe>
        </div>
      )}
    </div>
  );
};

export default App;
