import React, { useState, useEffect } from 'react';
import './App.css';

const BingoBoard = ({ markedNumbers }) => {
  return (
    <div className="bingo-board">
      {[...Array(90).keys()].map(i => {
        const number = i + 1;
        const isMarked = markedNumbers.includes(number);

        return (
          <div
            key={number}
            className={`bingo-number ${isMarked ? 'marked' : ''}`}
          >
            {number.toString().padStart(2, '0')}
          </div>
        );
      })}
    </div>
  );
};

const App = () => {
  const [currentNumber, setCurrentNumber] = useState(null);
  const [markedNumbers, setMarkedNumbers] = useState([]);
  const [previousNumbers, setPreviousNumbers] = useState([]);
  const [showVideo, setShowVideo] = useState(false);
  const [flash, setFlash] = useState(false);

  const generateRandomNumber = () => {
    const availableNumbers = [...Array(90).keys()].map(i => i + 1).filter(num => !markedNumbers.includes(num));
    if (availableNumbers.length === 0) return;

    const randomIndex = Math.floor(Math.random() * availableNumbers.length);
    const randomNumber = availableNumbers[randomIndex];

    setCurrentNumber(randomNumber);
    setMarkedNumbers(prev => [...prev, randomNumber]);
    setPreviousNumbers(prev => {
      if (currentNumber !== null) {
        return [currentNumber, ...prev].slice(0, 5);
      }
      return prev;
    });

    // Activa el destello solo para el número y desactívalo después de 0.5 segundos
    setFlash(true);
    setTimeout(() => setFlash(false), 500);

    // Retraso de 2 segundos antes de mostrar el video
    setShowVideo(false);
    setTimeout(() => {
      setShowVideo(true);
    }, 2000);
  };

  useEffect(() => {
    const handleKeyPress = (event) => {
      if (event.key === 'Enter') {
        generateRandomNumber();
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [currentNumber]);

  return (
    <div className="app-container">
      {/* Fondo con gradiente y blobs desenfocados */}
      <div className="background-gradient">
        <div className="blob blob-1"></div>
        <div className="blob blob-2"></div>
        <div className="blob blob-3"></div>
      </div>

      {/* Caja del Número Aleatorio */}
      <div className="current-number-box">
        <div className={`current-number ${flash ? 'flash-effect' : ''}`}>
          {currentNumber !== null ? currentNumber.toString().padStart(2, '0') : "00"}
        </div>
      </div>

      {/* Caja Lateral para los Últimos 5 Números */}
      <div className="side-box">
        <div className="side-content">
          {previousNumbers.map((num, index) => (
            num !== null && num !== undefined ? (
              <span key={index} className={`previous-number opacity-${index}`}>
                {num.toString().padStart(2, '0')}
              </span>
            ) : null
          ))}
        </div>
      </div>

      {/* Nueva Caja a la Derecha de side-box */}
      <div className="additional-box">
        <div className="additional-content">Contenido de la Nueva Caja</div>
      </div>

      {/* Caja Grande para el Tablero de Bingo */}
      <div className="large-box">
        <BingoBoard markedNumbers={markedNumbers} />
      </div>

      {/* Caja Pequeña debajo de large-box */}
      <div className="small-box">
        <div className="small-content">Contenido de la Caja Pequeña</div>
      </div>

      {/* Caja del Video con retraso de 2 segundos */}
      {showVideo && (
        <div className="video-box">
          <iframe
            src="https://www.youtube.com/embed/dQw4w9WgXcQ?autoplay=1"
            title="Video Panel"
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          ></iframe>
        </div>
      )}
    </div>
  );
};

export default App;
