import React, { useState, useEffect, useCallback } from 'react';
import './App.css';

const BingoBoard = ({ markedNumbers }) => {
  return (
    <div className="bingo-board">
      {[...Array(90).keys()].map(i => {
        const number = i + 1;
        const isMarked = markedNumbers.includes(number);

        return (
          <div
            key={number}
            className={`bingo-number ${isMarked ? 'marked' : ''}`}
          >
            {number.toString().padStart(2, '0')}
          </div>
        );
      })}
    </div>
  );
};

const VideoPanel = ({ show }) => {
  return (
    show ? (
      <div className="video-box">
        <iframe
          width="100%"
          height="100%"
          src="https://www.youtube.com/embed/dQw4w9WgXcQ?autoplay=1"
          title="Bingo Video"
          frameBorder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowFullScreen
        ></iframe>
      </div>
    ) : null
  );
};

const App = () => {
  const [currentNumber, setCurrentNumber] = useState(null);
  const [previousNumbers, setPreviousNumbers] = useState([]);
  const [showVideo, setShowVideo] = useState(false);
  const [markedNumbers, setMarkedNumbers] = useState([]);
  const [flash, setFlash] = useState(false); // Estado para controlar el destello

  const generateRandomNumber = useCallback(() => {
    const availableNumbers = [...Array(90).keys()].map(i => i + 1).filter(num => !previousNumbers.includes(num));
    if (availableNumbers.length === 0) return;

    const randomIndex = Math.floor(Math.random() * availableNumbers.length);
    const randomNumber = availableNumbers[randomIndex];

    setCurrentNumber(randomNumber);
    setPreviousNumbers(prev => [randomNumber, ...prev.slice(0, 4)]);
    setMarkedNumbers(prev => [...prev, randomNumber]);

    // Activamos el destello temporalmente
    setFlash(true);
    setTimeout(() => setFlash(false), 500); // Duración del destello

    setTimeout(() => setShowVideo(true), 1500); // Retraso de 1.5 segundos antes de mostrar el video
    setTimeout(() => setShowVideo(false), 40000); // Oculta el video después de 40 segundos
  }, [previousNumbers]);

  useEffect(() => {
    const handleKeyPress = (event) => {
      if (event.key === 'Enter') {
        generateRandomNumber();
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [generateRandomNumber]);

  return (
    <div className="app-container">
      <div className="current-number-box">
        <div className={`current-number ${flash ? 'flash' : ''}`}>
          {currentNumber !== null ? currentNumber.toString().padStart(2, '0') : "00"}
        </div>
      </div>
      <div className="side-box">
        <div className="side-content">
          {previousNumbers.slice(1, 6).map((num, index) => (
            <div key={index} className={`previous-number opacity-${index}`}>
              {num.toString().padStart(2, '0')}
            </div>
          ))}
        </div>
      </div>
      <div className="additional-box">
        <div className="additional-content">Información adicional</div>
      </div>
      <VideoPanel show={showVideo} />
      <div className="large-box">
        <BingoBoard markedNumbers={markedNumbers} />
      </div>
      <div className="small-box">
        <div className="small-content">Texto informativo en la caja pequeña</div>
      </div>
    </div>
  );
};

export default App;
