import React, { useState, useEffect, useCallback } from 'react';
import './App.css';

const BingoBoard = ({ markedNumbers, onNumberClick }) => {
  return (
    <div className="bingo-board">
      {[...Array(90).keys()].map(i => {
        const number = i + 1;
        const isMarked = markedNumbers.includes(number);

        return (
          <div
            key={number}
            className={`bingo-number ${isMarked ? 'marked' : ''}`}
            onClick={() => onNumberClick(number)}
            style={{ cursor: 'pointer' }}
          >
            {number.toString().padStart(2, '0')}
          </div>
        );
      })}
    </div>
  );
};

const VideoPanel = ({ show }) => {
  return (
    show ? (
      <div className="video-panel">
        <iframe
          width="100%"
          height="100%"
          src="https://www.youtube.com/embed/dQw4w9WgXcQ?autoplay=1" // Video de ejemplo
          title="Bingo Video"
          frameBorder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowFullScreen
        ></iframe>
      </div>
    ) : null
  );
};

const App = () => {
  const [currentNumber, setCurrentNumber] = useState(null);
  const [previousNumbers, setPreviousNumbers] = useState([]); // Números que van saliendo
  const [showVideo, setShowVideo] = useState(false);
  const [markedNumbers, setMarkedNumbers] = useState([]); // Números marcados en verde

  const generateRandomNumber = useCallback(() => {
    const availableNumbers = [...Array(90).keys()].map(i => i + 1).filter(num => !previousNumbers.includes(num));
    if (availableNumbers.length === 0) return;

    const randomIndex = Math.floor(Math.random() * availableNumbers.length);
    const randomNumber = availableNumbers[randomIndex];

    setCurrentNumber(randomNumber);
    setPreviousNumbers(prev => [randomNumber, ...prev].slice(0, 5)); // Mantiene los últimos 5 números
    setMarkedNumbers(prev => [...prev, randomNumber]); // Marca el número en verde

    setShowVideo(true); // Muestra el video cada vez que se genera un nuevo número
    setTimeout(() => setShowVideo(false), 40000); // Oculta el video después de 40 segundos
  }, [previousNumbers]);

  const handleNumberClick = (number) => {
    setCurrentNumber(number); // Muestra el número clicado en la casilla principal
    if (!markedNumbers.includes(number)) {
      setMarkedNumbers([...markedNumbers, number]); // Añade el número a los marcados si no lo está
      setPreviousNumbers(prev => [number, ...prev].slice(0, 5)); // Actualiza la lista de los últimos 5 números clicados
    }
    setShowVideo(true); // Muestra el video al hacer clic en un número
    setTimeout(() => setShowVideo(false), 40000); // Oculta el video después de 40 segundos
  };

  useEffect(() => {
    const handleKeyPress = (event) => {
      if (event.key === 'Enter') {
        generateRandomNumber();
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [generateRandomNumber]);

  return (
    <div
      className="app-container"
      style={{
        background: "url('/images/Bg.png') no-repeat center center fixed",
        backgroundSize: "cover",
      }}
    >
      <div className="current-number-box">
        <div className="current-number">
          {currentNumber !== null ? currentNumber.toString().padStart(2, '0') : "00"}
        </div>
      </div>
      <div className="previous-numbers-box">
        {previousNumbers.slice(0, 5).map((num, index) => (
          <div key={index} className="previous-number">
            {num.toString().padStart(2, '0')}
          </div>
        ))}
      </div>
      <VideoPanel show={showVideo} />
      <BingoBoard markedNumbers={markedNumbers} onNumberClick={handleNumberClick} />
    </div>
  );
};

export default App;
