import React, { useState, useEffect, useCallback } from 'react';
import './App.css';
import Logo from './images/Logo-UuhQE.png';

const BingoBoard = ({ markedNumbers }) => {
  return (
    <div className="bingo-board">
      {[...Array(90).keys()].map(i => {
        const number = i + 1;
        const isMarked = markedNumbers.includes(number);
        return (
          <div
            key={number}
            className={`bingo-number ${isMarked ? 'marked' : ''}`}
          >
            {number.toString().padStart(2, '0')}
          </div>
        );
      })}
    </div>
  );
};

const VideoPanel = ({ show, videoUrl }) => {
  return (
    show ? (
      <div className="video-box">
        <iframe
          width="100%"
          height="100%"
          src={`${videoUrl}?autoplay=1`}
          title="Bingo Video"
          frameBorder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowFullScreen
        ></iframe>
      </div>
    ) : null
  );
};

const App = () => {
  const [currentNumber, setCurrentNumber] = useState(null);
  const [previousNumbers, setPreviousNumbers] = useState([]);
  const [markedNumbers, setMarkedNumbers] = useState([]);
  const [showLiniaCantada, setShowLiniaCantada] = useState(false);
  const [animate, setAnimate] = useState(false);
  const [showVideo, setShowVideo] = useState(false);
  const [videoUrl, setVideoUrl] = useState('');

  const videoLinks = {
    1: 'https://www.youtube.com/embed/dQw4w9WgXcQ',
    2: 'https://www.youtube.com/embed/sample2',
    3: 'https://www.youtube.com/embed/sample3',
  };

  const generateRandomNumber = useCallback(() => {
    const availableNumbers = [...Array(90).keys()].map(i => i + 1).filter(num => !previousNumbers.includes(num));
    if (availableNumbers.length === 0) return;

    const randomIndex = Math.floor(Math.random() * availableNumbers.length);
    const randomNumber = availableNumbers[randomIndex];

    setCurrentNumber(randomNumber);
    setPreviousNumbers(prev => [randomNumber, ...prev].slice(0, 5));
    setMarkedNumbers(prev => [...prev, randomNumber]);

    setAnimate(true);
    setTimeout(() => setAnimate(false), 500);

    if (videoLinks[randomNumber]) {
      setVideoUrl(videoLinks[randomNumber]);
      setTimeout(() => setShowVideo(true), 1500);
      setTimeout(() => setShowVideo(false), 40000);
    }
  }, [previousNumbers]);

  useEffect(() => {
    const handleKeyPress = (event) => {
      if (event.key === 'Enter') {
        generateRandomNumber();
      }
      if (event.key === 'l' || event.key === 'L') {
        setShowLiniaCantada(prev => !prev);
        if (!showLiniaCantada) triggerEmojiRain();
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [generateRandomNumber, showLiniaCantada]);

  const triggerEmojiRain = () => {
    const emojis = ['🎉', '🥳', '🎈'];
    for (let i = 0; i < 30; i++) {
      const emoji = document.createElement('div');
      emoji.className = 'emoji';
      emoji.style.left = `${Math.random() * 100}vw`; // Posición aleatoria horizontal
      emoji.style.animationDelay = `${Math.random() * 0.5}s`; // Delay aleatorio para efecto lluvia
      emoji.textContent = emojis[Math.floor(Math.random() * emojis.length)];
      document.body.appendChild(emoji);

      emoji.addEventListener('animationend', () => {
        emoji.remove();
      });
    }
  };

  return (
    <div className="app-container">
      <div className="current-number-box">
        <div className={`current-number ${animate ? 'animate-flash' : ''}`}>
          {currentNumber !== null ? currentNumber.toString().padStart(2, '0') : "00"}
        </div>
      </div>

      <div className="side-box">
        <div className="side-content">
          {previousNumbers.slice(1, 6).map((num, index) => (
            <span key={index} className={`previous-number opacity-${index}`}>
              {num.toString().padStart(2, '0')}
            </span>
          ))}
        </div>
      </div>

      <VideoPanel show={showVideo} videoUrl={videoUrl} />

      <div className="large-box">
        <BingoBoard markedNumbers={markedNumbers} />
      </div>

      <div className="small-box">
        <span>01/06 - QUINA FAMA</span>
        <span className={`linia-cantada ${showLiniaCantada ? 'show' : ''}`}>LÍNIA CANTADA!! 🎉🎉</span>
      </div>

      <div className="additional-box">
        <img src={Logo} alt="Logo UuhQE" className="logo-image" />
      </div>
    </div>
  );
};

export default App;
