import React, { useState, useEffect } from 'react';
import './Bingo.css';

const Bingo = () => {
  const [sortedNumbers, setSortedNumbers] = useState(new Set());
  const [displayNumber, setDisplayNumber] = useState(null);

  // Generar un número aleatorio
  const generateRandomNumber = () => {
    const availableNumbers = [...Array(50).keys()].map(i => i + 1).filter(num => !sortedNumbers.has(num));
    if (availableNumbers.length === 0) return; // Todos los números ya han salido

    const randomIndex = Math.floor(Math.random() * availableNumbers.length);
    const randomNumber = availableNumbers[randomIndex];

    setSortedNumbers(prev => new Set(prev).add(randomNumber));
    setDisplayNumber(randomNumber);
  };

  // Detectar la tecla "Enter" para generar un número aleatorio
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'Enter') {
        generateRandomNumber();
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [sortedNumbers]);

  return (
    <div className="bingo-container">
      {/* Título y cuadro del número generado */}
      <div className="left-section">
        <h1 className="title">UuhQuina <span className="title-highlight">Cantada</span></h1>
        <div className="display-number">
          {displayNumber !== null ? displayNumber : 'Presiona Enter'}
        </div>
      </div>

      {/* Cuadrícula de números */}
      <div className="bingo-card">
        {[...Array(50).keys()].map(i => {
          const number = i + 1;
          const isSorted = sortedNumbers.has(number);

          return (
            <div key={number} className={`bingo-cell ${isSorted ? 'sorted' : ''}`}>
              {number}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Bingo;
