import React, { useState, useEffect } from 'react';
import './Bingo.css';

const Bingo = () => {
  const [sortedNumbers, setSortedNumbers] = useState(new Set());
  const [displayNumber, setDisplayNumber] = useState(null);
  const [showVideo, setShowVideo] = useState(false);

  // Función para generar un número aleatorio
  const generateRandomNumber = () => {
    const availableNumbers = [...Array(90).keys()].map(i => i + 1).filter(num => !sortedNumbers.has(num));
    if (availableNumbers.length === 0) return; // Todos los números ya han salido

    const randomIndex = Math.floor(Math.random() * availableNumbers.length);
    const randomNumber = availableNumbers[randomIndex];

    setSortedNumbers(prev => new Set(prev).add(randomNumber));
    setDisplayNumber(randomNumber);

    // Mostrar el video si el número es par
    if (randomNumber % 2 === 0) {
      setShowVideo(true);
      // Cierra el video automáticamente después de 40 segundos si sigue abierto
      setTimeout(() => setShowVideo(false), 40000);
    } else {
      setShowVideo(false); // Asegurarse de que el video esté cerrado si es un número impar
    }
  };

  // Detectar la tecla "Enter" para generar un número aleatorio y cerrar el video si está abierto
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'Enter') {
        if (showVideo) {
          setShowVideo(false); // Cerrar el video si se presiona Enter mientras el video está abierto
        }
        generateRandomNumber();
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [showVideo, sortedNumbers]);

  return (
    <div className="bingo-container">
      {/* Modal de video a pantalla completa cuando el número es par */}
      {showVideo && (
        <div className="video-modal" onClick={() => setShowVideo(false)}>
          <div className="video-modal-content">
            <div className="overlay-number">{displayNumber}</div>
            <iframe
              className="video-fullscreen"
              src="https://www.youtube.com/embed/uBFdSyDkPOU?autoplay=1"
              title="YouTube video player"
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            ></iframe>
          </div>
        </div>
      )}

      {/* Título y cuadro del número generado */}
      <div className="left-section">
        <h1 className="title">UuhQuina <span className="title-highlight">Cantada</span></h1>
        <div className="display-number">
          {displayNumber !== null ? displayNumber : 'Presiona Enter'}
        </div>
      </div>

      {/* Cuadrícula de 90 números */}
      <div className="bingo-card">
        {[...Array(90).keys()].map(i => {
          const number = i + 1;
          const isSorted = sortedNumbers.has(number);

          return (
            <div key={number} className={`bingo-cell ${isSorted ? 'sorted' : ''}`}>
              {number}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Bingo;
