import React, { useState, useEffect } from 'react';
import BingoBoard from './BingoBoard';
import InfoPanel from './InfoPanel';
import Timer from './Timer';
import VideoPanel from './VideoPanel';
import './App.css';

const App = () => {
  const [currentNumber, setCurrentNumber] = useState(null);
  const [previousNumbers, setPreviousNumbers] = useState([]);
  const [lineMessage, setLineMessage] = useState("");
  const [showVideo, setShowVideo] = useState(false);

  // Función para generar un número aleatorio entre 1 y 90
  const generateRandomNumber = () => {
    const availableNumbers = [...Array(90).keys()].map(i => i + 1).filter(num => !previousNumbers.includes(num));
    if (availableNumbers.length === 0) return;

    const randomIndex = Math.floor(Math.random() * availableNumbers.length);
    const randomNumber = availableNumbers[randomIndex];

    setCurrentNumber(randomNumber);
    setPreviousNumbers(prev => [randomNumber, ...prev.slice(0, 2)]); // Guardar los últimos 3 números
    if (randomNumber === 5) {
      setShowVideo(true);
      setTimeout(() => setShowVideo(false), 40000); // Mostrar video por 40 segundos
    }
  };

  // Mostrar mensaje de línea cantada al presionar "L"
  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      generateRandomNumber();
    } else if (event.key === 'l' || event.key === 'L') {
      setLineMessage("¡Línea CANTADA!");
      setTimeout(() => setLineMessage(""), 3000); // El mensaje dura 3 segundos
    }
  };

  useEffect(() => {
    window.addEventListener('keydown', handleKeyPress);
    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [previousNumbers]);

  return (
    <div className="app-container">
      <InfoPanel
        currentNumber={currentNumber}
        previousNumbers={previousNumbers}
        lineMessage={lineMessage}
      />
      <Timer />
      <BingoBoard currentNumber={currentNumber} />
      {showVideo && <VideoPanel />}
    </div>
  );
};

export default App;
